const ItemShow = ({items, handleDelete, idShow}) => {
    return ( 
        <div>
            {items.map(item => (
                <div key={item.id}>
                    <p>{item.type}</p>
                    <h1>{item.content}</h1>
                    <button onClick={() => handleDelete(item.id)}>delete item</button>
                </div>
            ))}
        </div>
     );
}

export default ItemShow;
