import React from 'react';
import { Button } from 'react-bootstrap';
import { createUseStyles } from 'react-jss';

const useStyles  = createUseStyles({
    heading: {
        fontSize: 50,
    },
    simplehighlighta: {
        backgroundColor: '#ff7709',
        borderRadius: 50,
    },
    simplehighlightb: {
        backgroundColor: '#b4ff44',
        borderRadius: 50,
    },
    carebear: {
        // backgroundColor: '#aa75ff',
        borderRadius: 50,
        fontSize: 20,
    },
    options: {
        // display: 'flex',
        // justifyContent: 'space-around'
    }

})

const Home = () => {
    const classes = useStyles()
    return ( 
        <div className="home">
            <h1 className={classes.heading}>
                Note-to-self<br />
                just checking in to make sure that you are feeling okay :)
            </h1>
            <Button>Adding messages for future you</Button>
            <Button>Reading message from yourself</Button>
        </div>
     );
}

export default Home;
